{% assign product = collections.all.products %}
{% style %}
.wrap {
  height: 100%;
  display: grid;
  justify-content: center;
}

.anchor {
  display: grid;
  width: 300px;
  height: 45px;
  font-family: Roboto, sans-serif';
  font-size: 11px;
  letter-spacing: 2.5px;
  font-weight: 500;
  color: white;
  justify-content: center;
  text-align: center;
  align-items: center;
  background-color: #35A173;
  border: none;
  border-radius: 15px;
  box-shadow: 0px 0px 0px; rgba(46, 229, 157, 0.4);
  transition: all 0.3s ease 0s;
  cursor: pointer;
  outline: none;
  }

.anchor:hover {
  transform: translateY(-7px);
}
{% endstyle %}
<div class="wrap">
  <a href="products/slidark-5" class="anchor">Plus de détails</a>
</div>

